        destroyPlayer() {
            return new Promise((resolve, reject) => {
                try {
                    if (this.jessibuca) {
                        this.jessibuca.destroy().then(() => {
                            this.jessibuca = null;
                            this.initPlayer();
                            this.playType = '';
                            resolve();
                        });
                    }
                } catch (e) {
                    console.error(e);
                    reject(e);
                }
            })
