        restartPlay(type) {

            if (type === 'mse') {
                this.useWCS = false;
                this.useOffscreen = false;
                this.useSIMD = false;
            } else if (type === 'wcs') {
                this.useMSE = false
                this.useSIMD = false;
            } else if (type === 'offscreen') {
                this.useMSE = false
            } else if (type === 'simd') {
                this.useMSE = false;
                this.useWCS = false;
            } else if (type === 'isNakedFlow') {
                this.isFlv = false;
                this.isFmp4 = false;
                this.isHls = false;
            } else if (type === 'isFlv') {
                this.isNakedFlow = false;
                this.isFmp4 = false;
                this.isHls = false;
            } else if (type === 'isFmp4') {
                this.isNakedFlow = false;
                this.isFlv = false;
                this.isHls = false;
            } else if (type === 'isHls') {
                this.isNakedFlow = false;
                this.isFlv = false;
                this.isFmp4 = false;
            }


            this.replay();
        },

        replay() {
            this.destroyPlayer().then(() => {
                if (this.playType === 'play') {
                    this.play();
                } else if (this.playType === 'playback') {
                    this.playback();
                } else if (this.playType === 'playVod') {
                    this.playVod();
                } else {
                    this.play();
                }
            });
        },

